<span class="line">    <span class="token literal-property property">url</span><span class="token operator">:</span> <span class="token string">'/dev-api/standarInterface/archives/'</span> <span class="token operator">+</span> row<span class="token punctuation">.</span>id<span class="token punctuation">,</span></span>
