<span class="line">    <span class="token keyword">if</span> <span class="token punctuation">(</span>formData<span class="token punctuation">.</span>email<span class="token punctuation">.</span><span class="token function">trim</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">===</span> <span class="token string">''</span><span class="token punctuation">)</span> <span class="token punctuation">{</span></span>
<span class="line"></span>
