<span class="line"></span>
<span class="line"></span>
<span class="line">      <span class="token punctuation">{</span><span class="token punctuation">(</span><span class="token parameter"><span class="token punctuation">{</span> isSubmitting <span class="token punctuation">}</span></span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">(</span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">        <span class="token operator">&lt;</span>Form<span class="token operator">></span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">          <span class="token operator">&lt;</span>div<span class="token operator">></span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">            <span class="token operator">&lt;</span>label htmlFor<span class="token operator">=</span><span class="token string">"username"</span><span class="token operator">></span>Username<span class="token operator">:</span><span class="token operator">&lt;</span><span class="token operator">/</span>label<span class="token operator">></span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">            <span class="token operator">&lt;</span>Field type<span class="token operator">=</span><span class="token string">"text"</span> id<span class="token operator">=</span><span class="token string">"username"</span> name<span class="token operator">=</span><span class="token string">"username"</span> <span class="token operator">/</span><span class="token operator">></span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">            <span class="token operator">&lt;</span>ErrorMessage name<span class="token operator">=</span><span class="token string">"username"</span> component<span class="token operator">=</span><span class="token string">"p"</span> <span class="token operator">/</span><span class="token operator">></span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">          <span class="token operator">&lt;</span><span class="token operator">/</span>div<span class="token operator">></span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">          <span class="token operator">&lt;</span>div<span class="token operator">></span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">            <span class="token operator">&lt;</span>label htmlFor<span class="token operator">=</span><span class="token string">"email"</span><span class="token operator">></span>Email<span class="token operator">:</span><span class="token operator">&lt;</span><span class="token operator">/</span>label<span class="token operator">></span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
