<span class="line">    <span class="token function-variable function">createUser</span><span class="token operator">:</span> <span class="token punctuation">(</span><span class="token parameter">parent<span class="token punctuation">,</span> <span class="token punctuation">{</span> name<span class="token punctuation">,</span> email <span class="token punctuation">}</span><span class="token punctuation">,</span> context<span class="token punctuation">,</span> info</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span></span>
