<span class="line"></span>
<span class="line"></span>
<span class="line">  <span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"> </span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">  <span class="token keyword">return</span> <span class="token punctuation">(</span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">    <span class="token operator">&lt;</span>View style<span class="token operator">=</span><span class="token punctuation">{</span>styles<span class="token punctuation">.</span>container<span class="token punctuation">}</span><span class="token operator">></span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">      <span class="token operator">&lt;</span>Text style<span class="token operator">=</span><span class="token punctuation">{</span>styles<span class="token punctuation">.</span>title<span class="token punctuation">}</span><span class="token operator">></span>React Native Example<span class="token operator">&lt;</span><span class="token operator">/</span>Text<span class="token operator">></span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">      <span class="token operator">&lt;</span>TextInput</span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">        style<span class="token operator">=</span><span class="token punctuation">{</span>styles<span class="token punctuation">.</span>input<span class="token punctuation">}</span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">        placeholder<span class="token operator">=</span><span class="token string">"Enter something"</span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">        value<span class="token operator">=</span><span class="token punctuation">{</span>text<span class="token punctuation">}</span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">        onChangeText<span class="token operator">=</span><span class="token punctuation">{</span>setText<span class="token punctuation">}</span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">      <span class="token operator">/</span><span class="token operator">></span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">      <span class="token operator">&lt;</span>Button title<span class="token operator">=</span><span class="token string">"Fetch Data"</span> onPress<span class="token operator">=</span><span class="token punctuation">{</span>fetchData<span class="token punctuation">}</span> <span class="token operator">/</span><span class="token operator">></span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">      <span class="token punctuation">{</span>loading <span class="token operator">&amp;&amp;</span> <span class="token operator">&lt;</span>ActivityIndicator size<span class="token operator">=</span><span class="token string">"large"</span> color<span class="token operator">=</span><span class="token string">"#0000ff"</span> <span class="token operator">/</span><span class="token operator">></span><span class="token punctuation">}</span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">      <span class="token punctuation">{</span>data <span class="token operator">&amp;&amp;</span> <span class="token punctuation">(</span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">        <span class="token operator">&lt;</span>FlatList</span>
